const registers = Array.isArray(msg.payload) ? msg.payload : msg.payload.data;

if (!Array.isArray(registers) || registers.length < 4) {
    node.error("Invalid register array", msg);
    return null;
}

const buffer = Buffer.alloc(registers.length * 2);

// Fill buffer (Big Endian: AB CD)
for (let i = 0; i < registers.length; i++) {
    buffer.writeUInt16BE(registers[i], i * 2);
}

// Convert to float32 every 4 bytes
const floats = [];
for (let i = 0; i + 3 < buffer.length; i += 4) {
    floats.push(buffer.readFloatBE(i));
}

msg.payload = floats;
return msg;

const registers = Array.isArray(msg.payload) ? msg.payload : msg.payload.data;

if (!Array.isArray(registers) || registers.length < 4) {
    node.error("Invalid register array", msg);
    return null;
}

const buffer = Buffer.alloc(registers.length * 2);

// Fill buffer (Big Endian: AB CD)
for (let i = 0; i < registers.length; i++) {
    buffer.writeUInt16BE(registers[i], i * 2);
}

// Convert to float32 every 4 bytes, rounded to 2 decimal places
const floats = [];
for (let i = 0; i + 3 < buffer.length; i += 4) {
    const floatVal = buffer.readFloatBE(i);
    floats.push(parseFloat(floatVal.toFixed(4)));
}

msg.payload = floats;
return msg;

const floats = msg.payload;

const temperature = floats[0];
const humidity = floats[1];
const pressure = floats[2];
const flowrate = floats[3];

// Force GMT+7 (e.g., Asia/Jakarta)
const now = new Date();
const offsetMs = 7 * 60 * 60 * 1000;
const localTime = new Date(now.getTime() + offsetMs);

// Format YYYY-MM-DD HH:mm:ss
const pad = n => n.toString().padStart(2, '0');
const timestamp = `${localTime.getFullYear()}-${pad(localTime.getMonth()+1)}-${pad(localTime.getDate())} ${pad(localTime.getHours())}:${pad(localTime.getMinutes())}:${pad(localTime.getSeconds())}`;

msg.topic = `
    INSERT INTO sensor_data 
    (vol_total, run_vol, row_rate, density, timestamp) 
    VALUES (?, ?, ?, ?, ?)
`;

msg.payload = [temperature, humidity, pressure, flowrate, timestamp];

return msg;

const picked = new Date(msg.payload);

// Adjust to local time (WIB/GMT+7)
const offsetMs = 7 * 60 * 60 * 1000;
const start = new Date(picked.getTime() + offsetMs);
const end = new Date(start.getTime() + 24 * 60 * 60 * 1000);

// Format to YYYY-MM-DD HH:mm:ss
const pad = n => n.toString().padStart(2, '0');
const format = d => `${d.getFullYear()}-${pad(d.getMonth()+1)}-${pad(d.getDate())} ${pad(d.getHours())}:${pad(d.getMinutes())}:${pad(d.getSeconds())}`;

const startStr = format(start);
const endStr = format(end);

// Build SQL
msg.topic = `
    SELECT * FROM sensor_data
    WHERE timestamp >= '${startStr}' AND timestamp < '${endStr}'
    ORDER BY timestamp DESC
`;

return msg;

const id = parseInt(msg.payload);

if (isNaN(id)) {
    node.warn("Invalid ID input");
    return null;
}

msg.topic = "SELECT * FROM sensor_data WHERE id = ?";
msg.payload = [id];
return msg;

const data = msg.payload[0];
msg.payload = data;
return msg;

msg.payload = {
    fc: 1,           // Function Code 1 = Read Coils
    unitid: 1,       // Change if your slave ID is different
    address: 1281,   // Coil address Y0
    quantity: 1      // Read 1 coil
};
return msg;

// Get previous state, default to false
let previous = context.get('prev') ?? false;

// Get current coil value from msg.payload[0]
let current = msg.payload[0];

// Save current value for next comparison
context.set('prev', current);

// Only allow msg to pass if transition is TRUE  FALSE
if (previous === true && current === false) {
    msg.payload = "Device just turned OFF!";
    return msg;
}

return null;  // Don't output anything for other cases

// Reuse the existing payload values
const [temperature, humidity, pressure, flowrate, timestamp] = msg.payload;

// Rearranged for spreadsheet
msg.payload = [
    timestamp,     // Column A
    temperature,   // Column B
    humidity,      // Column C
    pressure,      // Column D
    flowrate       // Column E
];

return msg;
